import styled, { css } from "styled-components";
import { COLORS, containerStyle, FONTS } from "../../assets/styles";

const inputStyle = css`
  background: ${COLORS.LIGHT_GREY};
  opacity: 0.6;
  border-radius: 4px;
  border: none;
  padding: 10px;
  font-family: ${FONTS.AVENIR};
  font-weight: 500;
  font-size: 15px;
  line-height: 18px;
  color: ${COLORS.DARK_GREY};
`;

export default {
  Container: styled.div`
    padding: 30px 120px;
    flex-grow: 1;
  `,
  Form: {
    Container: styled.form`
      ${containerStyle}
      padding: 17px 28px;
      margin-bottom: 22px;
    `,
    TitleInput: styled.input`
      ${inputStyle}
      margin-bottom: 10px;
    `,
    DescriptionInput: styled.textarea`
      ${inputStyle}
      margin-bottom: 17px;
    `,
    Button: styled.button`
      align-self: flex-end;
      height: 32px;
      padding: 6px 20px;
      width: 120px;
      display: flex;
      align-items: center;
      justify-content: center;
      background-color: ${COLORS.NIMBLE_RED};
      color: ${COLORS.WHITE};
      border: none;
      border-radius: 4px;
    `,
    Icon: styled.img`
      margin-right: 8px;
    `,
  },
  Todos: styled.div`
    padding: 25px;
    display: flex;
    flex-direction: column;
    box-shadow: 0px 4px 8px 4px rgba(0, 0, 0, 0.07);
    border-radius: 4px;
  `,
};
