import styled from "styled-components";
import { COLORS, containerStyle, FONTS } from "../../assets/styles";

export default {
  Container: styled.div`
    margin: 30px 120px;
    padding: 25px;
    width: 100%;
    height: fit-content;
    ${containerStyle};
  `,
  Filter: styled.div`
    display: flex;
    flex-direction: row;
    margin-bottom: 50px;
  `,
  Button: styled.div<{ active: "completed" | "canceled"; name: string }>`
    padding: 19px 0px;
    width: 50%;
    text-align: center;
    font-family: ${FONTS.SEGOE_UI};
    font-weight: 600;
    font-size: 14px;
    line-height: 19px;
    color: ${(props) => props.active === "completed" && COLORS.NIMBLE_RED};
    cursor: pointer;
    border-bottom: 2px solid ${COLORS.NIMBLE_RED};
  `,
};
