import React, { FunctionComponent, useState } from "react";
import Layout from "../layout/layout";
import Styles from "./history.styles";
import { useSelector } from "react-redux";
import Todo, { TodoProps } from "../common/todo/todo";

const selectTodos = (state: any) => state.todos;

const History: FunctionComponent = () => {
  const todos = useSelector(selectTodos);
  const [filter, setFilter] = useState<"completed" | "canceled">("completed");
  return (
    <Layout>
      <Styles.Container>
        <Styles.Filter>
          <Styles.Button>Terminées</Styles.Button>
          <Styles.Button>Supprimées</Styles.Button>
        </Styles.Filter>
        {todos.map(
          (todo: TodoProps) =>
            filter === "completed" &&
            todo.completed && (
              <Todo
                key={todo.id}
                id={todo.id}
                title={todo.title}
                description={todo.description}
              />
            )
        )}
      </Styles.Container>
    </Layout>
  );
};

export default History;
