import styled, { css } from "styled-components";
import { COLORS, FONTS } from "../../assets/styles";

const inputStyle = css`
  background: ${COLORS.LIGHT_GREY};
  opacity: 0.6;
  border-radius: 4px;
  border: none;
  padding: 10px;
  font-family: ${FONTS.AVENIR};
  font-weight: 500;
  font-size: 15px;
  line-height: 18px;
  color: ${COLORS.DARK_GREY};
`;
export default {
  Container: styled.div`
    padding: 30px 120px;
    flex-grow: 1;
  `,
  Form: {
    Container: styled.form`
      padding: 17px 28px;
      display: flex;
      flex-direction: column;
    `,
    TitleInput: styled.input`
      margin-bottom: 10px;
      background: ${COLORS.LIGHT_GREY};
      opacity: 0.6;
      border-radius: 4px;
      border: none;
      padding: 10px;
      font-family: ${FONTS.AVENIR};
      font-weight: 500;
      font-size: 15px;
      line-height: 18px;
      color: ${COLORS.DARK_GREY};
    `,
    DescriptionInput: styled.textarea`
      ${inputStyle}
      margin-bottom: 10px;
    `,
  },
};
