import { takeLatest, call, put } from "redux-saga/effects";
import { TodoAction } from "./components/todos/todos.slice";
import axios from "axios";

// watcher saga: watches for actions dispatched to the store, starts worker saga
export function* watcherSaga() {
  yield takeLatest("todos/fetchTodos", all);
  yield takeLatest("todos/addTodo", newTodo);
  yield takeLatest("todos/completeTodo", completeTodo);
  yield takeLatest("todos/archiveTodo", deleteTodo);
}

// function that makes the api request and returns a Promise for response
function getTodos() {
  return axios({
    method: "get",
    url: "http://localhost:8080/todos",
  });
}

function addTodo(action: TodoAction) {
  return axios({
    method: "post",
    url: "http://localhost:8080/todos",
    data: {
      title: action.payload.title,
      description: action.payload.description,
      completed: false,
      deleted: false,
    },
  });
}

function updateTodo(action: TodoAction) {
  return axios({
    method: "put",
    url: "http://localhost:8080/todos/complete/" + action.payload.id,
  });
}

function archiveTodo(action: TodoAction) {
  return axios({
    method: "put",
    url: "http://localhost:8080/todos/archive/" + action.payload.id,
  });
}

// worker saga: makes the api call when watcher saga sees the action
function* all() {
  try {
    const response = yield call(getTodos);
    const todos = response.data;
    // dispatch a success action to the store with the new todo
    yield put({ type: "todos/getTodos", payload: { todos } });
  } catch (error) {
    console.log(error);
    // dispatch a failure action to the store with the error
    yield put({ type: "todos/error", error });
  }
}

function* newTodo(action: TodoAction) {
  try {
    yield call(addTodo, action);
    // dispatch a success action to the store with the new todo
    yield put({ type: "todos/fetchTodos" });
  } catch (error) {
    console.log(error);
    // dispatch a failure action to the store with the error
    yield put({ type: "todos/error", error });
  }
}

function* completeTodo(action: TodoAction) {
  try {
    yield call(updateTodo, action);
    // dispatch a success action to the store with the new todo
    yield put({ type: "todos/fetchTodos" });
  } catch (error) {
    console.log(error);
    // dispatch a failure action to the store with the error
    yield put({ type: "todos/error", error });
  }
}

function* deleteTodo(action: TodoAction) {
  try {
    yield call(archiveTodo, action);
    // dispatch a success action to the store with the new todo
    yield put({ type: "todos/fetchTodos" });
  } catch (error) {
    console.log(error);
    // dispatch a failure action to the store with the error
    yield put({ type: "todos/error", error });
  }
}
